INSERT INTO Pais
    (nombre, descripcion) VALUES
    ('Alemania', 'La Repblica Federal de Alemania, comnmente conocida como Alemania, es un pas ubicado en el centro de Europa. Limita con Dinamarca y el Mar Bltico al norte, Polonia y la Repblica Checa al este, Austria y Suiza al sureste, Francia y Luxemburgo al suroeste, Blgica y los Pases Bajos al oeste. Alemania es conocida por su rica historia, su cultura vibrante y su economa prspera. Su capital es Berln.'),
    ('Argentina', 'La Repblica Argentina es un pas ubicado en el sur de Amrica del Sur. Limita con Chile al oeste, Bolivia y Paraguay al norte, Brasil al noreste y Uruguay al este. Argentina es conocida por su hermosa costa atlntica, sus montaas andinas y su rica cultura gauchesca. Su capital es Buenos Aires.'),
    ('Australia', 'La Mancomunidad de Australia es un pas ubicado en el suroeste de Oceana. Limita con el Ocano ndico al oeste y el Ocano Pacfico al este. Australia es conocida por sus playas de ensueo, sus desiertos ridos y su vida silvestre nica. Su capital es Canberra.'),
    ('Brazil', 'La Repblica Federativa del Brasil es el pas ms grande de Amrica del Sur. Limita con Venezuela y Guyana al norte, Surinam y Francia (Guayana Francesa) al noreste, la Repblica Cooperativa de Guyana al este, Colombia y Per al oeste y Bolivia y Paraguay al suroeste. Brasil es conocido por su rica biodiversidad, su cultura vibrante y su economa en crecimiento. Su capital es Brasilia.'),
    ('Canada', 'El Canad es un pas ubicado en el norte de Amrica del Norte. Limita con Estados Unidos al sur y el Ocano Artico al norte. Canad es conocido por sus montaas majestuosas, sus lagos cristalinos y su rica cultura inuit. Su capital es Ottawa.'),
    ('Chile', 'La Repblica de Chile es un pas ubicado en el suroeste de Amrica del Sur. Limita con Per al norte, Bolivia y Argentina al este, y el Ocano Pacfico al oeste. Chile es conocido por sus playas de ensueo, sus volcanes activos y su rica cultura mapuche. Su capital es Santiago.'),
    ('Colombia', 'La Repblica de Colombia es un pas ubicado en el noroeste de Amrica del Sur. Limita con Panam al norte, Venezuela al este, Brasil al sureste, Per y Ecuador al oeste y el Ocano Pacfico al suroeste. Colombia es conocido por su rica biodiversidad, su cultura vibrante y su economa en crecimiento. Su capital es Bogot.'),
    ('Francia', 'La Repblica Francesa es un pas ubicado en el centro de Europa. Limita con Blgica y Luxemburgo al noreste, Alemania y Suiza al este, Italia y Mnaco al sureste, Espaa y Andorra al sur, y el Ocano Atlntico al oeste. Francia es conocido por su rica historia, su cultura vibrante y su economa prspera. Su capital es Pars.'),
    ('India', 'La Repblica de la India es un pas ubicado en el sur de Asia. Limita con Pakistn al oeste, China, Nepal y Butn al norte, y Bangladesh y Myanmar al este. India es conocido por su rica cultura, su rica historia y su economa en crecimiento. Su capital es Nueva Delhi.'),
    ('Indonesia', 'La Repblica de Indonesia es un pas ubicado en el sudeste de Asia. Limita con Malasia, Singapur y Timor Oriental al oeste, y el Ocano ndico al sur. Indonesia es conocido por sus islas tropicales, sus volcanes activos y su rica cultura. Su capital es Yakarta.'),
    ('Italia', 'La Repblica Italiana es un pas ubicado en el centro de Europa. Limita con Francia y Suiza al norte, Austria y Eslovenia al noreste, Croacia y Montenegro al este, y Albania y Grecia al sureste. Italia es conocido por su rica historia, su cultura vibrante y su economa prspera. Su capital es Roma.'),
    ('Japon', 'El Imperio del Japn es un pas ubicado en el este de Asia. Limita con Corea del Norte y Corea del Sur al norte, y el Ocano Pacfico al este. Japn es conocido por sus montaas majestuosas, sus lagos cristalinos y su rica cultura. Su capital es Tokio.'),
    ('Corea del Sur', 'La Repblica de Corea es un pas ubicado en el este de Asia. Limita con Corea del Norte al norte, y el Mar Amarillo al este. Corea del Sur es conocido por su rica cultura, su rica historia y su economa en crecimiento. Su capital es Sel.'),
    ('Mexico', 'Los Estados Unidos Mexicanos es un pas ubicado en el centro de Amrica del Norte. Limita con Estados Unidos al norte, Guatemala, Belice y Honduras al sur, y el Ocano Pacfico al oeste y el Golfo de Mxico al este. Mxico es conocido por su rica cultura, su rica historia y su economa en crecimiento. Su capital es Ciudad de Mxico.'),
    ('Peru', 'La Repblica del Per es un pas ubicado en el oeste de Amrica del Sur. Limita con Ecuador al norte, Colombia al este, Brasil al sureste, Bolivia y Chile al sur, y el Ocano Pacfico al oeste. Per es conocido por su rica cultura, su rica historia y su economa en crecimiento. Su capital es Lima.'),
    ('Polonia', 'La Repblica de Polonia es un pas ubicado en el centro de Europa. Limita con Alemania al oeste, la Repblica Checa y Eslovaquia al sur, Ucrania y Bielorrusia al este, y Lituania y Rusia al norte. Polonia es conocido por su rica historia, su cultura vibrante y su economa en crecimiento. Su capital es Varsovia.'),
    ('Espana', 'El Reino de Espaa es un pas ubicado en el suroeste de Europa. Limita con Francia y Andorra al norte, Gibraltar y Marruecos al sur, y el Ocano Atlntico al oeste. Espaa es conocido por su rica cultura, su rica historia y su economa prspera. Su capital es Madrid.'),
    ('Turquia', 'La Repblica de Turqua es un pas ubicado en el suroeste de Asia y el sudeste de Europa. Limita con Grecia y Bulgaria al norte, Armenia, Azerbaiyn y Georgia al este, Irn al sureste, Irak y Siria al sur, y el Mar Mediterrneo al oeste. Turqua es conocido por su rica cultura, su rica historia y su economa en crecimiento. Su capital es Ankara.'),
    ('Estados Unidos', 'La Unin de Estados Americanos es un pas ubicado en el norte de Amrica del Norte. Limita con Canad al norte, Mxico al sur, y el Ocano Pacfico al oeste y el Ocano Atlntico al este. Estados Unidos es conocido por su rica cultura, su rica historia y su economa prspera. Su capital es Washington D.C.'),
    ('Venezuela', 'La Repblica Bolivariana de Venezuela es un pas ubicado en el norte de Amrica del Sur. Limita con Colombia al oeste, Brasil al sur, Guyana y Surinam al este, y el Mar Caribe al norte. Venezuela es conocido por su rica biodiversidad, su cultura vibrante y su economa en crecimiento. Su capital es Caracas.')
